mapper.select:
select sc.*, scpr.product_id as cid from sms_coupon sc inner join sms_coupon_history sch on sch.coupon_id= sc.id inner join sms_coupon_product_relation scpr on sch.coupon_id = scpr.coupon_id where sch.member_id=#{userId} and scpr.product_id in
#{item}
union all select sc.*, scpc.product_category_id as cid from sms_coupon sc inner join sms_coupon_history sch on sch.coupon_id= sc.id inner join sms_coupon_product_category_relation scpc on sch.coupon_id = scpc.coupon_id where sch.member_id=#{userId} and scpc.product_category_id in
#{item}
union all select sc.*, '' as cid from sms_coupon sc inner join sms_coupon_history sch on sch.coupon_id= sc.id where sch.member_id=#{userId} and not exists(select 1 from sms_coupon_product_relation scpr where sch.coupon_id = scpr.coupon_id) and not exists(select 1 from sms_coupon_product_category_relation scpc where sch.coupon_id = scpc.coupon_id)
